import { useState } from 'react';
import { Button, Modal } from 'antd';

import './tasksAddForm.scss';

const TasksAddForm = ({onAdd}) => {

    const [name, setName] = useState('');
    const [date, setDate] = useState('')
    const [isModalOpen, setIsModalOpen] = useState(false);

    const showModal = () => {
      setIsModalOpen(true);
    };
    const handleOk = () => {
      setIsModalOpen(false);
    };
    const handleCancel = () => {
      setIsModalOpen(false);
    };

    function onNameChange(value) {
        setName(value)
    }

    function onDateChange(value) {
        setDate(value)
    }

    function onSubmit(e) {
        e.preventDefault();
        if (name && date && name.length > 3) {
            onAdd(name, date);
            setName('');
            setDate('');
        }
    }

    return(
        <>
        <Button onClick={showModal}>Добавить</Button>
        <Modal title="Basic Modal" open={isModalOpen}  onCancel={handleCancel} onClick={showModal}>
            <div className="app-add-form">
                <h3>Добавьте новую задачу</h3>
                <form 
                    className="add-form d-flex"
                    onSubmit={(e) => onSubmit(e)}>
                    <input type="text"
                        className='form-control new-post-label'
                        placeholder='Название задачи' 
                        name='name'
                        value={name}
                        onChange={(e) => onNameChange(e.target.value)}/>
                    <input type="date"
                        className='form-control new-post-label'
                        placeholder='Выберите дату' 
                        name='date'
                        value={date}
                        onChange={(e) => onDateChange(e.target.value)}/>
                    <button type='submit'
                            className='btn btn-outline-light'
                            onClick={showModal}>Добавить</button>
                </form>
            </div>
        </Modal>
        </>
    );

}

export default TasksAddForm;
