import { useState } from 'react';
import TasksListItem from '../tasksListItem/TasksListItem';

import './workSpace.scss';

const WorkSpace = ({data, editItem, item, addItem}) => {

    const [name, setName] = useState(item.name);
    const [descr, setDescr] = useState(item.descr)

    function onEdit(e) {
        e.preventDefault();
        if (item.name && item.name.length > 3) {
            addItem(item.name, item.descr, item.id);
            setName('');
            setDescr('');

        }
    }

    function onNameChange(value) {
        console.log(value)
        setName(value)
    }

    function onDescrChange(value) {
        console.log(value)
        setDescr(value)
    }

    return (
        <div className='workSpace'>
            <form action="">
                <input type='text' name='name' value={name} onChange={(e) => onNameChange(e.target.value)} defaultValue={item.name}/>
                <input type='text' name='descr' value={descr} onChange={(e) => onDescrChange(e.target.value)} defaultValue={item.descr}/>
                <button>Закрыть</button>
                <button onSubmit={(e) => onEdit(e)}>Сохранить</button> 
            </form>
        </div> 

    )
}

export default WorkSpace;
