import { Component } from 'react';

import AppInfo from '../app-info/app-info';
import SearchPanel from '../search-panel/search-panel';
import AppFilter from '../app-filter/app-filter';
import MusiciansList from '../musicians-list/musicians-list';
import MusiciansAddForm from '../musicians-add-form/musicians-add-form';

import './app.scss';

class App extends Component{
    constructor(props) {
        super(props);
        this.state = {
            data: [
                {name: "Ozzy Crazytrainov", salary: 800, increase: true, rise: true, id: 1},
                {name: "Tony Doomov", salary: 3000, increase: false, rise: false, id: 2},
                {name: "Bill Urod", salary: 5000, increase: false, rise: false, id: 3}
            ],
            term: '',
            filter: 'all'
        }

        this.maxId = 4;
    }

    deleteItem = (id) => {
        this.setState(({data}) => {
            return {
                data: data.filter(item => item.id !== id)
            }
        })
    }

    addItem = (name, salary) => {
        const newItem = {
            name,
            salary,
            increase: false,
            rise: false,
            id: this.maxId++
        }

        this.setState(({data}) => {
            const newArr = [...data, newItem];
            return{
                data: newArr
            }
        })
    }

    onToggleProp = (id, prop) => {
        this.setState(({data}) => ({
            data: data.map(item => {
                if (item.id === id) {
                    return{...item, [prop]: !item[prop]}
                }
                return item;
            })
        }))
    }

    searchMus = (items, term) => {
        if (term.length === 0) {
            return items;
        }

        return items.filter(item => {
            return item.name.toLowerCase().indexOf(term) > -1
        })
    }

    onUpdateSearchs = (term) => {
        this.setState({term});
    }

    filterPost = (items, filter) => {
        switch (filter) {
            case 'rise':
                return items.filter(item => item.rise);
            case 'more1000':
                return items.filter(item => item.salary > 1000);
            default:
                return items
        }
    }

    onFilterSelect = (filter) => {
        this.setState({filter});
    }


    render() {
        const {data, term, filter} = this.state;
        const musicians = this.state.data.length;
        const increased = this.state.data.filter(item => item.increase).length;
        const visibleData = this.filterPost(this.searchMus(data, term), filter);

        return(
            <div className="app">
                <AppInfo
                musicians={musicians}
                increased={increased}/>

                <div className="search-panel">
                    <SearchPanel onUpdateSearchs={this.onUpdateSearchs}/>
                    <AppFilter filter={filter} onFilterSelect={this.onFilterSelect}/>
                </div>

                <MusiciansList 
                    data={visibleData}
                    onDelete={this.deleteItem}
                    onToggleProp={this.onToggleProp}/>
                <MusiciansAddForm onAdd={this.addItem}/>
            </div>
        );
    }

}

export default App;




