import { useState } from 'react';
import TasksListItem from '../tasksListItem/TasksListItem';

import './workSpace.scss';

const WorkSpace = ({data, editItem, id, item, addItem}) => {

    const [name, setName] = useState('');
    const [descr, setDescr] = useState('')

    function onSubmit(e) {
        e.preventDefault();
        if (item.name && item.name.length > 3) {
            addItem(item.name, item.descr);
            // setName('');
            // setDescr('');

        }
    }

    function onNameChange(value) {
        setName(value)
    }

    function onDescrChange(value) {
        setDescr(value)
    }

    return (
        <div className='workSpace'>
            <form action="">
                <input onChange={(e) => onNameChange(e.target.value)} defaultValue={item.name}/>
                <input onChange={(e) => onDescrChange(e.target.value)} defaultValue={item.descr}/>
                <button>Закрыть</button>
                <button onSubmit={(e) => onSubmit(e)}>Сохранить</button> 
            </form>
        </div> 

    )
}

export default WorkSpace;
