import { useState } from 'react';
import { Button, Modal } from 'antd';

import './tasksAddForm.scss';

const TasksAddForm = ({onAdd}) => {

    const [name, setName] = useState('');
    const [date, setDate] = useState('')
    const [loading, setLoading] = useState(false);
    const [open, setOpen] = useState(false);

    const showModal = () => {
      setOpen(true);
    };

    const handleOk = () => {
      setLoading(true);
      setTimeout(() => {
        setLoading(false);
        setOpen(false);
      }, 3000);
    };

    const handleCancel = () => {
      setOpen(false);
    };

    function onNameChange(value) {
        setName(value)
    }

    function onDateChange(value) {
        setDate(value)
    }

    function onSubmit(e) {
        e.preventDefault();
        if (name && date && name.length > 3) {
            onAdd(name, date);
            setName('');
            setDate('');
        }
    }

    return(
        <>
        <Button onClick={showModal}>
            Добавить
        </Button>
        <Modal
            open={open}
            title="Title"
            onOk={handleOk}
            onCancel={handleCancel}
            footer={[
            <Button key="back" onClick={handleCancel}>
                Закрыть
            </Button>,
            <Button key="submit" type='submit' loading={loading} onClick={handleOk} onSubmit={(e) => onSubmit(e)}>
                Добавить
            </Button>
        ]}>
            <div className="app-add-form">
                <h3>Добавьте новую задачу</h3>
                <form 
                    className="add-form d-flex"
                    onSubmit={(e) => onSubmit(e)}>
                    <input type="text"
                        className='form-control new-post-label'
                        placeholder='Название задачи' 
                        name='name'
                        value={name}
                        onChange={(e) => onNameChange(e.target.value)}/>
                    <input type="date"
                        className='form-control new-post-label'
                        placeholder='Выберите дату' 
                        name='date'
                        value={date}
                        onChange={(e) => onDateChange(e.target.value)}/>
                    {/* <button type='submit'
                            className='btn btn-outline-light'
                            >Добавить</button> */}
                                        <Button key="submit" type='submit' loading={loading} onClick={handleOk} onSubmit={(e) => onSubmit(e)}>
                Добавить
            </Button>
                </form>
            </div>
        </Modal>


        {/* <Modal title="Basic Modal" onOk={handleOk} onCancel={handleCancel} onClick={showModal} type='submit'>
            <div className="app-add-form">
                <h3>Добавьте новую задачу</h3>
                <form 
                    className="add-form d-flex"
                    onSubmit={(e) => onSubmit(e)}>
                    <input type="text"
                        className='form-control new-post-label'
                        placeholder='Название задачи' 
                        name='name'
                        value={name}
                        onChange={(e) => onNameChange(e.target.value)}/>
                    <input type="date"
                        className='form-control new-post-label'
                        placeholder='Выберите дату' 
                        name='date'
                        value={date}
                        onChange={(e) => onDateChange(e.target.value)}/>
                    <button type='submit'
                            className='btn btn-outline-light'
                            >Добавить</button>
                </form>
            </div>
        </Modal> */}
        </>
    );

}

export default TasksAddForm;
