import { useState, useEffect } from 'react';
import TasksListItem from '../tasksListItem/TasksListItem';

import './workSpace.scss';

const WorkSpace = ({item, onAdd, data, deleteItem}) => {

    const [name, setName] = useState('');
    const [descr, setDescr] = useState('');



    useEffect(() => {
        setName(item.name ? item.name : '')
        setDescr(item.descr ? item.descr : '')

    },[item])

    function onEdit(e) {
        e.preventDefault();
        if (name && name.length > 3) {
            onAdd(name, descr);
            setName('');
            setDescr('');
            deleteItem(item.id);
        }


    }

    function onNameChange(value) {
        console.log(value)
        setName(value)
    }

    function onDescrChange(value) {
        console.log(value)
        setDescr(value)
    }

    return (
        <div className='workSpace'>
            <form action="">
                <input type='text' name='name' onChange={(e) => onNameChange(e.target.value)} value={name}/>
                <input type='text' name='descr' onChange={(e) => onDescrChange(e.target.value)}  value={descr}/>
                <button>Закрыть</button>
                <button onClick={(e) => onEdit(e)}>Сохранить</button> 
            </form>
        </div> 

    )
}

export default WorkSpace;
