import './editTaskWindow.scss';



const EditTaskWindow = ({data, id}) => {

    let newId;

    function getId(id) {
        data.map(item => {
            if (item.id === id) {
                newId = id;
            }
        })
    }

    console.log(data[newId].name);
    return(
        <div className='editWindow'>
            <div className='blabla'>
                <p>{data[newId].name}</p>
                <p>{data[newId].descr}</p>

            </div>

        </div>
    )


}

export default EditTaskWindow;

function onToggleEdit(id) {
    const elem = document.querySelector('.editWindow');
    setData(
        data.map(item => {
            if (item.id === id) {
                elem.classList.toggle('show')
            }
            return item;
        })
    )

    console.log(id)
}
