import { Component } from 'react';

import './search-panel.css';

class SearchPanel extends Component{
    constructor(props) {
        super(props);
        this.state = {
            term: ''
        }
    }

    onUpdateSearch = (e) => {
        const term = e.target.value;
        this.setState({term})
    }

    render() {
        return(
            <input 
                type="text"
                className="form-control search-input"
                placeholder="Найти чувака"/>
        )
    }
}

export default SearchPanel;
