import { Component, useState } from 'react';

import './musicians-add-form.scss';

const MusiciansAddForm = (props) => {

    // state = {
    //     name: '',
    //     salary: ''
    // }

    const [name, setName] = useState('');
    const [salary, setSalary] = useState('')


    function onNameChange(value) {
        // this.setState({
        //     [e.target.name]: e.target.value
        // })
        // useState(target.name) = target.value
        setName(value)
    }

    function onSalaryChange(value) {
        // this.setState({
        //     [e.target.name]: e.target.value
        // })
        // useState(target.name) = target.value
        setSalary(value)
    }

    function onSubmit(e) {
        e.preventDefault();
        if (name && salary && name.length > 3) {
            this.props.onAdd(name, salary);
            setName('');
            setSalary('')
        }

    }

        return(
            <div className="app-add-form">
                <h3>Добавьте новую задачу</h3>
                <form 
                    className="add-form d-flex"
                    onSubmit={() => onSubmit()}>
                    <input type="text"
                        className='form-control new-post-label'
                        placeholder='Название задачи' 
                        name='name'
                        value={name}
                        onChange={(e) => onNameChange(e.target.value)}/>
                    <input type="number"
                        className='form-control new-post-label'
                        placeholder='З/П в $?' 
                        name='salary'
                        value={salary}
                        onChange={(e) => onSalaryChange(e.target.value)}/>
                    <button type='submit'
                            className='btn btn-outline-light'>Добавить</button>
                </form>
            </div>
        );

}

export default MusiciansAddForm;



