import { useState, useEffect } from 'react';

import AppInfo from '../appInfo/AppInfo';
import TasksList from '../tasksList/TasksList';
import WorkSpace from '../workSpace/WorkSpace';



import 'antd/dist/reset.css';

import './app.scss';

const App = (props) => {

    const [data, setData] = useState([
        {name: "Подумать о важном", descr: 'кто я? а чего я хочу?', date: new Date(2022, 2, 16).toLocaleDateString(), state: true, important: false, id: 1},
        {name: "Побывать в горах Индии", descr: 'понять кто я и чего я хочу', date: new Date(2021, 11, 16).toLocaleDateString(), state: false, important: false, id: 2},
        {name: "Нанять Петю на работу", descr: 'ну точно нельзя проходить мимо этого парня', date: new Date(2022, 12, 20).toLocaleDateString(), state: false, important: true, id: 3}
    ]);

    const [term, setTerm] = useState('');   
    const [filter, setFilter] = useState('all');
    const [maxId, setMaxId] = useState(4);
    const [item, setItem] = useState('');

    // useEffect(() => {
    //     console.log('deleted')
    //     editItem(item.id)
    //     deleteItem(item.id)
    // }, [item])


    function deleteItem(id) {
        setMaxId(maxId - 1);
        return setData(data.filter(item => item.id !== id))
    }

    function editItem(id) {
        console.log(id)
        data.map(item => {
            if (item.id === id) {
                setItem(item);
            }    
        })
    }


    function addItem(name, descr, id) {
        setMaxId(maxId + 1);
        const newItem = {
            name: name,
            descr: descr,
            date: new Date().toLocaleDateString(), 
            state: false,
            important: false,
            id: id ? id : maxId
        }

        data.forEach(item => {
            if (item.id === id) {
                deleteItem(item)
            }
            return data;
        })

        const newArr = [...data, newItem];



        return setData(newArr);

    }

    function onToggleProp(id, prop) {
        setData(
            data.map(item => {
                if (item.id === id) {
                    return {...item, [prop]: !item[prop]}
                }
                return item;
            })
        )
    }

    function searchTask(items, term) {
        if (term.length === 0) {
            return items;
        }

        return items.filter(item => {
            return item.name.toLowerCase().indexOf(term) > -1
        })
    }

    function onUpdateSearchs(term) {
        setTerm(term);
    }

    function filterPost(items, filter) {
        switch (filter) {
            case 'important':
                return items.filter(item => item.important);
            case 'state':
                return items.filter(item => item.state);
            default:
                return items
        }
    }

    function onFilterSelect(filter) {
        setFilter(filter);    
    }

    const tasks = data.length;
    const state = data.filter(item => item.state).length;
    const visibleData = filterPost(searchTask(data, term), filter);


    return(
        <div className="app">
            <AppInfo
                tasks={tasks}
                state={state}
                onAdd={addItem}
            />

            <TasksList 
                data={visibleData}
                onDelete={deleteItem}
                onToggleProp={onToggleProp}
                onUpdateSearchs={onUpdateSearchs}
                onFilterSelect={onFilterSelect}
                filter={filter}
                editItem={editItem}
            />

            <WorkSpace
                data={data} 
                item={item} 
                onAdd={addItem} 
                deleteItem={deleteItem}
            />

        </div>
    );

}

export default App;
