import { Component, useState } from 'react';

import AppInfo from '../app-info/app-info';
import SearchPanel from '../search-panel/search-panel';
import AppFilter from '../app-filter/app-filter';
import MusiciansList from '../musicians-list/musicians-list';
import MusiciansAddForm from '../musicians-add-form/musicians-add-form';

import './app.scss';

const App = (props) => {


    const [data, setData] = useState([
        {name: "Ozzy Crazytrainov", salary: 800, increase: true, rise: true, id: 1},
        {name: "Tony Doomov", salary: 3000, increase: false, rise: false, id: 2},
        {name: "Bill Urod", salary: 5000, increase: false, rise: false, id: 3}
    ]);

    const [term, setTerm] = useState('');
    const [filter, setFilter] = useState('all');
    let [maxId, setMaxId] = useState(4);

    function deleteItem(id) {
        // this.setState(({data}) => {
        //     return {
        //         data: data.filter(item => item.id !== id)
        //     }
        // })
        return setData(data.filter(item => item.id !== id))
    }

    function addItem(name, salary) {
        const newItem = {
            name: 'jack',
            salary: 5000,
            increase: false,
            rise: false,
            id: maxId++
        }

        // this.setState(({data}) => {
        //     const newArr = [...data, newItem];
        //     return{
        //         data: newArr
        //     }
        // })

        const newArr = [...data, newItem];

        return setData(newArr);

    }

    function onToggleProp(id, prop) {
        // this.setState(({data}) => ({
        //     data: data.map(item => {
        //         if (item.id === id) {
        //             return{...item, [prop]: !item[prop]}
        //         }
        //         return item;
        //     })
        // }))

        setData(
            data.map(item => {
                if (item.id === id) {
                    return {...item, [prop]: !item[prop]}
                }
                return item;
            })
        )
    }

    function searchMus(items, term) {
        if (term.length === 0) {
            return items;
        }

        return items.filter(item => {
            return item.name.toLowerCase().indexOf(term) > -1
        })
    }

    function onUpdateSearchs(term) {
        // this.setState({term});
        setTerm(term);
    }

    function filterPost(items, filter) {
        switch (filter) {
            case 'rise':
                return items.filter(item => item.rise);
            case 'more1000':
                return items.filter(item => item.salary > 1000);
            default:
                return items
        }
    }

    function onFilterSelect(filter) {
        // this.setState({filter});
        setFilter(filter);    
    }



        const musicians = data.length;
        const increased = data.filter(item => item.increase).length;
        const visibleData = filterPost(searchMus(data, term), filter);

        return(
            <div className="app">
                <AppInfo
                musicians={musicians}
                increased={increased}/>

                <div className="search-panel">
                    <SearchPanel onUpdateSearchs={onUpdateSearchs}/>
                    <AppFilter filter={filter} onFilterSelect={onFilterSelect}/>
                </div>

                <MusiciansList 
                    data={visibleData}
                    onDelete={deleteItem}
                    onToggleProp={(e) => onToggleProp(e)}/>
                <MusiciansAddForm onAdd={() => addItem()}/>
            </div>
        );

}

export default App;



