import { useState } from 'react';

import AppInfo from '../appInfo/AppInfo';
import SearchPanel from '../searchPanel/SearchPanel';
import AppFilter from '../appFilter/AppFilter';
import TasksList from '../tasksList/TasksList';
import TasksAddForm from '../tasksAddForm/TasksAddForm';
import { Button, DatePicker, Space, version } from "antd";
import 'antd/dist/reset.css';

import './app.scss';

const App = (props) => {

    const [data, setData] = useState([
        {name: "Подумать о важном", date: new Date(2022, 2), state: true, important: false, id: 1},
        {name: "Побывать в горах Индии", date: '26-06-2023', state: false, important: false, id: 2},
        {name: "Нанять Петю на работу", date: '25-11-2022', state: false, important: true, id: 3}
    ]);

    const [term, setTerm] = useState('');   
    const [filter, setFilter] = useState('all');
    const [maxId, setMaxId] = useState(4);


    function deleteItem(id) {
        setMaxId(maxId - 1);
        return setData(data.filter(item => item.id !== id))
    }

    function addItem(name, date) {
        setMaxId(maxId + 1);
        const newItem = {
            name: name,
            date: date, 
            state: false,
            important: false,
            id: maxId
        }

        const newArr = [...data, newItem];

        return setData(newArr);

    }

    function onToggleProp(id, prop) {
        setData(
            data.map(item => {
                if (item.id === id) {
                    return {...item, [prop]: !item[prop]}
                }
                return item;
            })
        )
    }

    function searchMus(items, term) {
        if (term.length === 0) {
            return items;
        }

        return items.filter(item => {
            return item.name.toLowerCase().indexOf(term) > -1
        })
    }

    function onUpdateSearchs(term) {
        setTerm(term);
    }

    function filterPost(items, filter) {
        switch (filter) {
            case 'important':
                return items.filter(item => item.important);
            case 'state':
                return items.filter(item => item.state);
            default:
                return items
        }
    }

    function onFilterSelect(filter) {
        setFilter(filter);    
    }

    const tasks = data.length;
    const state = data.filter(item => item.state).length;
    const visibleData = filterPost(searchMus(data, term), filter);

    return(
        <div className="app">
            <AppInfo
            tasks={tasks}
            state={state}
            onAdd={addItem}
            />

            {/* <div className="search-panel">
                <SearchPanel onUpdateSearchs={onUpdateSearchs}/>
                <AppFilter filter={filter} onFilterSelect={onFilterSelect}/>
            </div> */}

            <TasksList 
                data={visibleData}
                onDelete={deleteItem}
                onToggleProp={onToggleProp}/>
            {/* <TasksAddForm onAdd={addItem}/> */}
        </div>
    );

}

export default App;
