import { useState } from 'react';

import TasksAddForm from '../tasksAddForm/TasksAddForm';
import { Button, Modal } from 'antd';

import './appInfo.scss';

const AppInfo = ({tasks, state, addItem}) => {

    const [isModalOpen, setIsModalOpen] = useState(false);
    const showModal = () => {
      setIsModalOpen(true);
    };
    const handleOk = () => {
      setIsModalOpen(false);
    };
    const handleCancel = () => {
      setIsModalOpen(false);
    };

    return(
        <div className="app-info">
            <h1>WomanUp ToDo List</h1>
            <h2>Общее число делишек: {tasks}</h2>
            <h2>Выполненных делишек: {state}</h2>
            <Button onClick={showModal}>Добавить</Button>
            <Modal title="Basic Modal" open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
                <TasksAddForm onAdd={addItem}/>
            </Modal>
        </div>
    );
}

export default AppInfo;
